import React from 'react';
import { Button, Collapse, Typography } from 'antd';
import { PlusSquareOutlined } from '@ant-design/icons';
import useFirestore from '../../hooks/useFirestore';

const { Panel } = Collapse;

const RoomList = () => {
   // Document room
   // {
   //    name: 'room name',
   //    description: 'mo ta',
   //    members: [uid1, uid2, ...]
   // }
   const rooms = useFirestore('rooms', {
      fieldName: 'members',
      operator: 'array-c'
   });

   return (
      <Collapse ghost defaultActiveKey={['1']}>
         <Panel header="Danh sách các phòng" key="1">
            <Typography.Link className="room">Room 1</Typography.Link>
            <Typography.Link className="room">Room 2</Typography.Link>
            <Typography.Link className="room">Room 3</Typography.Link>
            <Button type="text" icon={<PlusSquareOutlined />} className="add-room">Thêm phòng</Button>
         </Panel>
      </Collapse>
   );
}

export default RoomList;
