import React, { useContext } from 'react';
import { Alert, Avatar, Button, Form, Input, Tooltip } from 'antd';
import { UserAddOutlined } from '@ant-design/icons/lib/icons';
import { AppContext } from '../../Context/AppProvider';
import Message from './Message';

const ChatWindow = () => {
   const { selectedRoom, members, setIsInviteMemberVisible } = useContext(AppContext);

   return (
      <div className="chatwindow">
         {
            selectedRoom.id ? (
               <>
                  <div className="chatwindow-header">
                     <div className="room_info">
                        <p className="room_name">{selectedRoom.name}</p>
                        <span className="room_description">{selectedRoom.description}</span>
                     </div>
                     <div className="users_info">
                        <Button 
                           icon={<UserAddOutlined />}
                           type="text"
                           onClick={() => setIsInviteMemberVisible(true)}
                        >
                           Mời
                        </Button>
                        <Avatar.Group size="small" maxCount={2}>
                           {
                              members.map(member => 
                                 <Tooltip title={member.displayName} key={member.id}>
                                    <Avatar src={member.photoURL}>
                                       {member.photoURL ? '' : member.displayName.charAt(0).toUppercase()}
                                    </Avatar>
                                 </Tooltip>   
                              )
                           }
                        </Avatar.Group>
                     </div>
                  </div>
                  <div className="chatwindow-content">
                     <div className="message">
                        <Message text="Test" displayName="Nam" createAt={12741413} photoURL={null}/>
                        <Message text="Test" displayName="Nam" createAt={12741413} photoURL={null}/>
                        <Message text="Test" displayName="Nam" createAt={12741413} photoURL={null}/>
                        <Message text="Test" displayName="Nam" createAt={12741413} photoURL={null}/>
                     </div>
                     <Form className="form_message">
                        <Form.Item>
                           <Input 
                              bordered={false}
                              autoComplete="off"
                              placeholder="Nhập tin nhắn..."
                           />
                        </Form.Item>
                        <Button type="primary">Gửi</Button>
                     </Form>
                  </div>
               </>
            ) : <Alert 
                  message='Hãy chọn phòng'
                  type='info'
                  showIcon
                  
               />
         }
         
      </div>
   );
}

export default ChatWindow;
